mapper.select:
select pict_no as pictNo, camp_no as campNo, filename , filepath , filegrade from camp_picture
camp_no=#{campNo}
and filegrade = #{filegrade}

select
	*
from site


		 camp_no=#{campNo}


  		and reserve_date = #{reserveDate}

select camp_no as campNo from site where camp_no in
#{value}
and RESERVE_DATE = '0000-00-00'

select rnum, campNo, campName, campAddr, memberNo, campShow, campType, campPh, campPeriod, campDay, campFacility from (select rownum as rnum, n.* from (select camp_no as campNo, camp_name as campName, camp_addr as campAddr, member_no as memberNo, camp_show as campShow, camp_type as campType, camp_ph as campPh, camp_period as campPeriod, camp_day as campDay, camp_facility as campFacility from camp where camp_no in
#{value}
order by 1 desc) n) where rnum between #{start} and #{end}
